
public class Exercise1 {

	/**
	 * 1. Find out what other primitive types exist in Java and discover differences

	between them. All primitive types have names beginning with lowercase

	letters, just like int. There are 7 more for you to find (8 if you count

	void, which means “no type”).
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//8 bit
		byte four = 127;
		
		//16 bit
		short three = 32767;
		
		//32 bit
		int number;
		number = 3;
		
		double one = 123983458.6;
		
		//0-65535
		char a;
		a = 0;
		
		//64bit base 2
		int zero = 0;
		long five = 378934567; 
		boolean alt = false;
		
		//more complicated numbers
		//float six = 6.9998644;
		
		int obj = 0;
		if (obj == zero){
			alt = true;
		}
		if (obj == 0){
			alt = true;
		}
		obj = 0;
		if (obj == 0){
			alt = true;
		}
		

	}

}
